/* ==UserStyle==
@name         New Youtube Dracula Theme
@namespace    USO Archive
@author       steccah
@description  Based on "style 180682"
@version      20221104.14.45
@license      CC-BY-4.0
@preprocessor uso
==/UserStyle== */
@-moz-document domain("youtube.com"), regexp("https://*.youtube.com/*"), regexp("https://www.youtube.com/") {
/* i really want this to be global */
html:not(.style-scope)[dark], :not(.style-scope)[dark] {
    --yt-spec-brand-background-solid: #11111b;
    --yt-spec-brand-background-primary: #11111b;
    --yt-spec-brand-background-secondary: rgba(255, 255, 255, .25);
    --yt-spec-general-background-a: #11111b;
    --yt-spec-general-background-b: #11111b;
    --yt-spec-general-background-c: #11111b;
    --yt-spec-error-background: #f9f9f9;
    --yt-spec-text-primary: #cab1ec;
    --yt-spec-text-primary-inverse: #11111b;
    --yt-spec-text-secondary: #e5f1b3;
    --yt-spec-text-disabled: #717171;
    --yt-spec-call-to-action: #bd93f9;
    --yt-spec-icon-active-other: #fff;
    --yt-spec-icon-inactive: #7dea99;
    --yt-spec-icon-disabled: #978e8e;
    --yt-spec-badge-chip-background: rgba(255, 255, 255, 0.1);
    --yt-spec-verified-badge-background: rgba(255, 255, 255, 0.25);
    --yt-spec-suggested-action: #252a3a;
    --yt-spec-button-chip-background-hover: rgba(255, 255, 255, 0.2);
    --yt-spec-touch-response: #fff;
    --yt-spec-paper-tab-ink: rgba(255, 255, 255, .2);
    --yt-spec-filled-button-text: #030303;
    --yt-spec-call-to-action-inverse: #6272a4;
    --yt-spec-brand-icon-active: #fff;
    --yt-spec-brand-icon-inactive: #cfe08c;
    --yt-spec-brand-button-background: #cfe08c;
    --yt-spec-brand-link-text: #ff5555;
    --yt-spec-filled-button-focus-outline: rgba(255, 255, 255, 0.7);
    --yt-spec-call-to-action-button-focus-outline: rgba(62, 166, 255, 0.3);
    --yt-spec-brand-text-button-focus-outline: rgba(255, 78, 69, 0.3);
    --yt-spec-inactive-text-button-focus-outline: #606060;
    --yt-spec-ad-indicator: #a09;
    --yt-spec-brand-subscribe-button-background: var(--yt-spec-brand-button-background);
    --yt-spec-wordmark-text: #fff;
    --yt-spec-10-percent-layer: rgba(255, 255, 255, 0.1);
    --yt-spec-selected-nav-text: #fff;
    --yt-spec-themed-blue: #d7f16e;
    --yt-spec-themed-green: #50fa7b;
    --yt-spec-themed-overlay-background: rgba(0, 0, 0, .74);
    --ytd-searchbox-background: #181825;
    --yt-spec-base-background: #181825;
}

:root {
    --bg: #2e3340;
    --bg-light: #3b4252;
    --bg-lighter: #434c5e; 
    --bg-lightest: #4c556a;
    --bg-transparent: #2e3340f1;
    --accent: #5e81ac;
    --accent-light: #81a1c1;
    --accent-lighter: #88c0d0;    
    --accent-lightest: #8fbcbb;
    --text-color: #d8dee9;
    --green: #a3be8c;
    --red: #bf616a;
    --purple: #cab1ec;
}



ytd-masthead {
    --paper-input-container-input-align: baseline;
    display: block;
    width: 100%;
    background: var(--yt-spec-brand-background-primary);
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
    opacity: 1;
    background-color: #1f1f2f;
}
.guide-entry-badge.ytd-guide-entry-renderer {
    color: var(--yt-spec-brand-link-text);
}
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: var(--yt-spec-themed-blue);
}
#notification-count.ytd-notification-topbar-button-renderer {
    background-color: var(--yt-spec-themed-blue);
}
.sbdd_b, .sbsb_a, .sbfl_b, .sbsb_c {
    background: var(--yt-spec-brand-background-primary);
}
.gsfs, .sbfl_b {
    color: #fff;
}
.sbsb_d {
    background-color: var(--yt-spec-general-background-c);
}
.sbdd_b {
    border: 1px solid transparent;
}

.ytp-swatch-background-color {
    background-color: var(--purple);
}


html[darker-dark-theme][dark] #home-container-skeleton {
  background-color: var(--yt-spec-base-background) !important;
}

html[dark] #home-page-skeleton .skeleton-bg-color {
  background-color: #1e1e2f;
}

html[darker-dark-theme][dark] #guide-skeleton {
  background-color: var(--yt-spec-base-background);
}

html[darker-dark-theme][dark] #home-chips {
  background-color: var(--yt-spec-base-background);
}
}

/* ==UserStyle==
@name           Gruvbox For Youtube
@namespace      github.com/touhidulshawan/gruvbox-youtube
@version        1.0.1
@description    A gruvbox theme for Youtube
@license        MIT
==/UserStyle== */
@-moz-document domain("youtube.com") {
    html {
        --background-primary: #1d2021;
        --background-secondary: #32302f;
        --background-tertiary: #1d2021;
        --current-line: #282828;
        --selection: #202020;
        --foreground: #ebdbb2;
        --comment: #928374;
        --inactive: #bdae93;
        --cyan: #689d6a;
        --green: #98971a;
        --orange: #d65d0e;
        --pink: #b16286;
        --purple: #bd93f9;
        --red: #cc241d;
        --yellow: #d79921;
        --blue: #83a598;
    }

    html:not(.style-scope)[dark],
    :not(.style-scope)[dark] {
        --yt-spec-brand-background-primary: var(--background-secondary);
        --yt-spec-brand-background-secondary: var(--background-tertiary);
        --yt-spec-brand-background-solid: var(--background-primary);
        --yt-spec-general-background-a: var(--background-tertiary);
        --yt-spec-general-background-b: var(--background-secondary);
        --yt-spec-general-background-c: var(--background-primary);
        --yt-spec-error-background: var(--red);
        --yt-spec-text-primary: var(--foreground);
        --yt-spec-text-primary-inverse: var(--background-primary);
        --yt-spec-text-secondary: var(--comment);
        --yt-spec-text-disabled: var(--comment);
        --yt-spec-call-to-action: var(--orange);
        --yt-spec-icon-active-other: var(--yellow);
        --yt-spec-icon-inactive: var(--inactive);
        --yt-spec-icon-disabled: var(--comment);
        --yt-spec-badge-chip-background: var(--background-secondary);
        --yt-spec-verified-badge-background: var(--blue);
        --yt-spec-suggested-action: var(--background-tertiary);
        --yt-spec-button-chip-background-hover: var(--selection);
        --yt-spec-touch-response: var(--foreground);
        --yt-spec-paper-tab-ink: var(--pink);
        --yt-spec-filled-button-text: var(--orange);
        --yt-spec-call-to-action-inverse: var(--cyan);
        --yt-spec-brand-icon-active: var(--green);
        --yt-spec-brand-icon-inactive: var(--inactive);
        --yt-spec-brand-button-background: var(--orange);
        --yt-spec-brand-link-text: var(--green);
        --yt-spec-filled-button-focus-outline: var(--background-tertiary);
        --yt-spec-call-to-action-button-focus-outline: var(--blue);
        --yt-spec-brand-text-button-focus-outline: var(--red);
        --yt-spec-inactive-text-button-focus-outline: var(--inactive);
        --yt-spec-ad-indicator: var(--cyan);
        --yt-spec-brand-subscribe-button-background: var(--orange);
        --yt-spec-wordmark-text: var(--blue);
        --yt-spec-10-percent-layer: var(--background-secondary);
        --yt-spec-selected-nav-text: var(--selection);
        --yt-spec-themed-blue: var(--blue);
        --yt-spec-themed-green: var(--green);
        --yt-spec-themed-overlay-background: var(--background-tertiary);
        --yt-spec-base-background: var(--background-primary);
        --ytd-searchbox-background: var(--background-secondary);
        --ytd-searchbox-legacy-border-color: var(--cyan);
        --ytd-searchbox-legacy-button-border-color: var(--cyan);
        --ytd-searchbox-legacy-button-color: var(--blue);
    }

    #container.ytd-searchbox:hover,
    #container.ytd-searchbox:focus {
        border: 2px solid var(--blue);
    }

    #search-icon-legacy.ytd-searchbox {
        border-radius: 0;
    }
    #search-icon-legacy.ytd-searchbox:hover,
    #search-icon-legacy.ytd-searchbox:focus {
        background: var(--purple);
        border: 2px solid var(--purple);
    }
    ytd-searchbox[system-icons] #search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
        color: var(--background-primary);
        font-weight: bolder;
    }

    tp-yt0app-drawer {
        background: var(--background-primary);
    }

    ytd-masthead {
        --paper-input-container-input-align: baseline;
        display: block;
        width: 100%;
        background: var(--background-primary);
    }

    paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
        opacity: 1;
        background-color: var(--background-primary);
    }

    .guide-entry-badge.ytd-guide-entry-renderer {
        color: var(--yt-spec-brand-link-text);
    }

    #progress.ytd-thumbnail-overlay-resume-playback-renderer {
        background-color: var(--yt-spec-themed-blue);
    }

    #notification-count.ytd-notification-topbar-button-renderer {
        background-color: var(--yt-spec-themed-blue);
    }

    #button.yt-icon-button {
        color: var(--cyan);
    }
    .sbdd_b,
    .sbsb_a,
    .sbfl_b,
    .sbsb_c {
        background: var(--yt-spec-brand-background-primary);
    }

    .gsfs,
    .sbfl_b {
        color: var(--foreground);
    }

    .sbsb_d {
        background-color: var(--yt-spec-general-background-c);
    }

    .sbdd_b {
        border: 1px solid transparent;
    }

    .sbpqs_a {
        color: var(--pink);
    }

    .sbsb_i {
        color: var(--blue);
    }

    .sbfl_b {
        color: var(--cyan);
    }

    #cinematics {
        display: none;
    }
    .ytp-swatch-background-color {
    background-color: var(--cyan);
}

}

